export default class Timer {
    constructor() {
        this.timeDisplay = document.querySelector('.time-display');
        this.startButton = document.getElementById('start');
        this.resetButton = document.getElementById('reset');
        this.currentTimeDisplay = document.getElementById('current-time');
        this.mottoElement = document.getElementById('motto');
        this.timePresetButtons = document.querySelectorAll('.time-preset-btn');
        
        this.selectedMinutes = 25;
        this.timeLeft = this.selectedMinutes * 60;
        this.isRunning = false;
        this.interval = null;
        
        this.mottos = [
            "今天的努力是明天的基础",
            "学习是一场永不止步的旅程",
            "每一分钟都是提升自己的机会",
            "坚持的人才能看到最美的风景",
            "知识就是力量，学习创造未来",
            "不要让懒惰偷走你的时间",
            "学习是一种习惯，也是一种态度",
            "专注当下，收获未来",
            "把时间浪费在美好的事物上",
            "学习让生活更有意义"
        ];
        this.currentMottoIndex = 0;
        
        this.initializeEventListeners();
        this.updateDisplay();
        this.startClock();
        this.startMottoRotation();
    }
    
    initializeEventListeners() {
        this.startButton.addEventListener('click', () => this.toggleTimer());
        this.resetButton.addEventListener('click', () => this.resetTimer());
        
        this.timePresetButtons.forEach(button => {
            button.addEventListener('click', () => {
                if (!this.isRunning) {
                    const minutes = parseInt(button.dataset.minutes);
                    this.setActivePreset(button);
                    this.selectedMinutes = minutes;
                    this.timeLeft = minutes * 60;
                    this.updateDisplay();
                }
            });
        });
    }
    
    setActivePreset(activeButton) {
        this.timePresetButtons.forEach(button => {
            button.classList.remove('active');
        });
        activeButton.classList.add('active');
    }
    
    toggleTimer() {
        if (this.isRunning) {
            this.pauseTimer();
        } else {
            this.startTimer();
        }
    }
    
    startTimer() {
        this.isRunning = true;
        this.startButton.textContent = '暂停';
        this.timePresetButtons.forEach(button => {
            button.disabled = true;
        });
        
        this.interval = setInterval(() => {
            this.timeLeft--;
            this.updateDisplay();
            
            if (this.timeLeft <= 0) {
                this.completeTimer();
            }
        }, 1000);
    }
    
    pauseTimer() {
        this.isRunning = false;
        this.startButton.textContent = '开始';
        clearInterval(this.interval);
    }
    
    resetTimer() {
        this.pauseTimer();
        this.timeLeft = this.selectedMinutes * 60;
        this.timePresetButtons.forEach(button => {
            button.disabled = false;
        });
        this.updateDisplay();
    }
    
    completeTimer() {
        this.pauseTimer();
        this.timePresetButtons.forEach(button => {
            button.disabled = false;
        });
        alert('时间到！');
        
        // 播放提示音
        const audio = new Audio('data:audio/wav;base64,');
        audio.play();
    }
    
    updateDisplay() {
        const minutes = Math.floor(this.timeLeft / 60);
        const seconds = this.timeLeft % 60;
        this.timeDisplay.textContent = `${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
    }
    
    startClock() {
        this.updateCurrentTime();
        setInterval(() => this.updateCurrentTime(), 1000);
    }
    
    updateCurrentTime() {
        const now = new Date();
        const hours = String(now.getHours()).padStart(2, '0');
        const minutes = String(now.getMinutes()).padStart(2, '0');
        const seconds = String(now.getSeconds()).padStart(2, '0');
        this.currentTimeDisplay.textContent = `${hours}:${minutes}:${seconds}`;
    }
    
    startMottoRotation() {
        this.updateMotto();
        setInterval(() => this.updateMotto(), 5000);
    }
    
    updateMotto() {
        this.mottoElement.classList.remove('show');
        setTimeout(() => {
            this.mottoElement.textContent = this.mottos[this.currentMottoIndex];
            this.mottoElement.classList.add('show');
            this.currentMottoIndex = (this.currentMottoIndex + 1) % this.mottos.length;
        }, 500);
    }
}

// 初始化计时器
document.addEventListener('DOMContentLoaded', () => {
    new Timer();
}); 